mapper.select:
select count(*)
  from friend 
 where id=#{id}

select count(*)
  from friend 
 where nickname=#{nickname}

select salt, del, deldate
  from friend 
 where id=#{id}

select id, pw, nickname, name, tel, email, college, major, auth, del, regidate, introduce
  from friend 
 where id=#{id} and pw=#{pw}

mapper.update:
update friend
   set del=0, deldate=null
 where id=#{id}
