mapper.select:
select count(*)
  from friend 
 where id=#{id}

select salt, del, deldate
  from friend 
 where id=#{id}

select id, pw, name, tel, email, college, auth, del, regidate
  from friend 
 where id=#{id} and pw=#{pw}

mapper.update:
update friend
   set del=0, deldate=null
 where id=#{id}
