mapper.select:
select seq, id, title, content, major, college, rdate, auth, wdate
  from calendar
 where seq=#{seq}

mapper.update:
update calendar
   set title=#{title}, content=#{content}
 where id=#{id} and seq=${seq}

mapper.select:
select seq, id, content, wdate
  from free
 where id=#{id}

select seq, id, title, content, wdate, del, readcount
  from free
 where id=#{id}

select seq, blockid, word, reason, adddate
  from blacklist
 where id=#{id}

mapper.update:
update member
   set name=#{name}, email=#{email}
 where id=#{id}

mapper.select:
select id, name, email, auth 
  from member 
 where id=#{id}

mapper.update:
update friend
   set del=del+1, deldate=now()
 where id=#{id}
