mapper.select:
select seq, id, content, wdate
  from bbscomment
 where id=#{id}

select seq, id, title, content, wdate, del, readcount
  from bbs
 where id=#{id}

select seq, blockid, word, reason, adddate
  from blacklist
 where id=#{id}

mapper.update:
update member
   set name=#{name}, email=#{email}
 where id=#{id}

mapper.select:
select id, name, email, auth 
  from member 
 where id=#{id}

mapper.update:
update friend
   set del=del+1, deldate=now()
 where id=#{id}
